type MCPClientManager struct {
    client client.Client
    ctx    context.Context
    cancel context.CancelFunc
}

func NewMCPClientManager(clientType, address string) (*MCPClientManager, error) {
    var c client.Client
    var err error

    switch clientType {
    case &quot;stdio&quot;:
        c, err = client.NewStdioClient(&quot;server-command&quot;)
    case &quot;http&quot;:
        c = client.NewHTTPClient(address)
    case &quot;sse&quot;:
        c = client.NewSSEClient(address)
    default:
        return nil, fmt.Errorf(&quot;unknown client type: %s&quot;, clientType)
    }

    if err != nil {
        return nil, err
    }

    ctx, cancel := context.WithCancel(context.Background())

    manager := &amp;MCPClientManager{
        client: c,
        ctx:    ctx,
        cancel: cancel,
    }

    // Initialize connection
    if err := c.Initialize(ctx); err != nil {
        cancel()
        return nil, fmt.Errorf(&quot;failed to initialize client: %w&quot;, err)
    }

    return manager, nil
}

func (m *MCPClientManager) Close() error {
    m.cancel()
    return m.client.Close()
}

