class MCPHTTPClient {
    constructor(baseURL, options = {}) {
        this.baseURL = baseURL;
        this.headers = {
            &#x27;Content-Type&#x27;: &#x27;application/json&#x27;,
            ...options.headers
        };
        this.requestId = 1;
    }

    setAuthToken(token) {
        this.headers[&#x27;Authorization&#x27;] = `Bearer ${token}`;
    }

    async sendRequest(method, params = {}) {
        const id = this.requestId++;
        const request = {
            jsonrpc: &#x27;2.0&#x27;,
            id,
            method,
            params
        };

        const response = await fetch(`${this.baseURL}/${method.replace(&#x27;/&#x27;, &#x27;/&#x27;)}`, {
            method: &#x27;POST&#x27;,
            headers: this.headers,
            body: JSON.stringify(request)
        });

        if (!response.ok) {
            throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }

        const result = await response.json();

        if (result.error) {
            throw new Error(result.error.message);
        }

        return result.result;
    }

    async initialize() {
        return this.sendRequest(&#x27;initialize&#x27;, {
            protocolVersion: &#x27;2024-11-05&#x27;,
            capabilities: { tools: {} },
            clientInfo: { name: &#x27;Web Client&#x27;, version: &#x27;1.0.0&#x27; }
        });
    }

    async listTools() {
        return this.sendRequest(&#x27;tools/list&#x27;);
    }

    async callTool(name, arguments) {
        return this.sendRequest(&#x27;tools/call&#x27;, { name, arguments });
    }

    async listResources() {
        return this.sendRequest(&#x27;resources/list&#x27;);
    }

    async readResource(uri) {
        return this.sendRequest(&#x27;resources/read&#x27;, { uri });
    }
}

// Usage
const client = new MCPHTTPClient(&#x27;http://localhost:8080/mcp&#x27;);
client.setAuthToken(&#x27;your-jwt-token&#x27;);

async function main() {
    try {
        await client.initialize();

        const tools = await client.listTools();
        console.log(&#x27;Available tools:&#x27;, tools);

        const result = await client.callTool(&#x27;search_users&#x27;, {
            query: &#x27;john&#x27;,
            limit: 10
        });
        console.log(&#x27;Search result:&#x27;, result);

    } catch (error) {
        console.error(&#x27;Error:&#x27;, error);
    }
}

main();

