            mcp.WithInteger(&quot;n&quot;, mcp.Required(), mcp.Minimum(0)),
        ),
        p.handleFactorial,
    )

    s.AddTool(
        mcp.NewTool(&quot;math.fibonacci&quot;,
            mcp.WithDescription(&quot;Calculate Fibonacci number&quot;),
            mcp.WithInteger(&quot;n&quot;, mcp.Required(), mcp.Minimum(0)),
