func (p *MathPlugin) RegisterResources(s *server.MCPServer) error {
    s.AddResource(
        mcp.NewResource(
            &quot;math://constants/{name}&quot;,
            &quot;Mathematical Constants&quot;,
            mcp.WithResourceDescription(&quot;Common mathematical constants&quot;),
            mcp.WithMIMEType(&quot;application/json&quot;),
        ),
        p.handleConstants,
    )

    return nil
